mapper.select:
SELECT *
FROM USERS
WHERE USERNAME = #{username}
AND PASSWORD = #{password};

mapper.insert:
INSERT INTO USERS
    (USERNAME, PASSWORD, EMAIL, ADDR, PHONE)
VALUES(#{username}, #{password}, #{email}, #{address}, #{phone});

mapper.update:
UPDATE ADMIN SET
    PASSWORD = #{password},
    PHONE = #{phone}
WHERE USERNAME = #{username};

mapper.select:
SELECT  *
FROM  ADMIN
WHERE USERNAME = #{username}
AND PASSWORD = #{password};
