mapper.select:
SELECT *
FROM ORDERS
WHERE USERNAME = #{username}
ORDER BY ODERID
DESC LIMIT 1;

SELECT *
FROM ORDERS
WHERE USERNAME = #{username}
ORDER BY ORDERDATE;

mapper.insert:
INSERT INTO ORDERS
(USERNAME, ORDERDATA, TOTALCOUNT, TOTALPRICE, PAID, SENDTO, CHECKOUT)
VALUES(#{username}, #{orderDate}, #{totalCount}, #{totalPrice}, #{paid}, #{sendTo},#{checkout});
